// Login.js

import React, { useState } from 'react';
import { loginUser } from '../services/api';
import { useNavigate } from 'react-router-dom';
import '../styles/Login.css';  // 引入相对路径
import { TextField, Button, Container, Typography, Box } from '@mui/material';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await loginUser(username, password);
      localStorage.setItem('user', JSON.stringify(response.data)); // 保存用户信息
      navigate('/tasks'); // 登录成功后跳转到任务列表页面
    } catch (err) {
      setError('Login failed');
    }
  };

  return (
    <Container component="main" maxWidth="md" className="login-container">
    <Box className="login-box" sx={{ boxShadow: 3, borderRadius: 2 }}>
      <Grid container>
        <Grid item xs={12} md={6} className="login-form-section">
          <Typography component="h1" variant="h5" gutterBottom align="center" className="login-title">
            Login
          </Typography>
          {error && (
            <Typography color="error" variant="body2" align="center">
              {error}
            </Typography>
          )}
          <Box component="form" onSubmit={handleLogin} noValidate sx={{ mt: 1 }}>
            <TextField
              margin="normal"
              required
              fullWidth
              id="username"
              label="Username"
              name="username"
              autoComplete="username"
              autoFocus
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              InputProps={{
                startAdornment: (
                  <i className="fa fa-user icon" style={{ color: '#fff' }} />
                ),
              }}
            />
            <TextField
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              InputProps={{
                startAdornment: (
                  <i className="fa fa-lock icon" style={{ color: '#fff' }} />
                ),
              }}
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              className="login-btn"
              sx={{ mt: 3, mb: 2 }}
            >
              Login
            </Button>
            <Typography variant="body2" align="center" className="signup-text">
              Don't have an account?{' '}
              <a href="/register" className="signup-link">
                Sign Up
              </a>
            </Typography>
          </Box>
        </Grid>
        <Grid item xs={12} md={6} className="login-welcome-section">
          <Box sx={{ textAlign: 'center' }}>
            <Typography component="h1" variant="h4" gutterBottom className="welcome-title">
              WELCOME BACK!
            </Typography>
            <Typography variant="body1" className="welcome-text">
              Lorem ipsum dolor sit amet consectetur adipisicing.
            </Typography>
          </Box>
        </Grid>
      </Grid>
    </Box>
  </Container>
  );
};

export default Login;
